(2, 'document', 'text/html', 'Прайс', '', '', 'prajs', '', 1, 0, 0, 65, 1, '', '<p><span style="color: #057ec8;"><strong>Что понимается под одной операцией?</strong></span></p>\r\n<p>- до 10 проводок по банку в день;</p>\r\n<p>- закрывающие документы (акт, счет &ndash; фактура, товарная накладная и прочее);</p>\r\n<p>- выписка из банка за один календарный день;</p>\r\n<p>- z &ndash; отчет.</p>\r\n<p><span style="color: #057ec8;">Прайс- лист</span></p>\r\n<p></p>\r\n<p></p>', 1, 15, 1, 1, 0, 1, 1393912570, 1, 1396637268, 0, 0, 0, 1393912570, 1, '', 0, 0, 0, 0, 0, 0, 0, 1),
(3, 'document', 'text/html', 'Заказать', '', '', 'zakazat', '', 1, 0, 0, 65, 1, '', '<p>Как заказать наши услуги</p>\r\n<ul>\r\n<li>Мы работаем с понедельника по пятницу c 09:00 до 19:00.</li>\r\n<li>Контактные телефоны: +7 (499) 112-02-42</li>\r\n<li>Электронная почта: <a href="mailto:info@ufreg.com">info@ufreg.com</a></li>\r\n</ul>', 1, 16, 2, 1, 0, 1, 1393912626, 1, 1496729039, 0, 0, 0, 1393912626, 1, '', 0, 0, 0, 0, 0, 0, 1, 1),
(4, 'document', 'text/html', 'Контакты', '', '', 'kontaktyi', '', 1, 0, 0, 65, 1, '', '<h3>Компания РЕГИСТРАЛ расположена по адресу:</h3>\r\n<p>м. Кожуховская, БЦ Золотое кольцо, ул. Южнопортовая, д. 5, стр. 1.</p>\r\n<p>&nbsp;</p>\r\n<h3>Контактные телефоны:</h3>\r\n<p>+7 (499) 112-02-42</p>\r\n<p>E-Mail: <a href="mailto:info@ufreg.com">info@ufreg.com</a></p>\r\n<p>&nbsp;</p>\r\n<h3>График работы:</h3>\r\n<p>Понедельник-четверг с 9-00 до 19-00. Пятница с 9-00 по 18-00</p>\r\n<p><img src="assets/images/map_ufreg.png" alt="Карта проезда" style="display: block; margin-left: auto; margin-right: auto;" /></p>', 1, 8, 4, 1, 0, 1, 1393912648, 1, 1498740794, 0, 0, 0, 1393912648, 1, '', 0, 0, 0, 0, 0, 0, 0, 1),
