(22, 'getTable', 'Вставляем таблицу если необходимо', 0, 9, 0, '\r\nif(!isset($parent))return ''памылка'';\r\n	if(isset($wont_table) && !empty($wont_table)){\r\n	echo ''<table class="price"  cellspacing="0" cellpadding="0">\r\n				<tr>\r\n					<th>Вид услуги</th> \r\n					<th>Описание</th> \r\n					<th>Стоимость</th> \r\n					<th>&nbsp;</th>\r\n				</tr> '';\r\n$elements = $modx->getActiveChildren($parent);\r\n$el='''';\r\n$last = false;	\r\nforeach ($elements as $item){		\r\n	$el = $modx->getTemplateVarOutput(array(''colspan'', ''price'',''view_vichet'',''link'',''content'',''pick_out'',''addButton''), $item[''id'']);		$str = "";		\r\n	if($el[''colspan''] == 1)\r\n		$str = ''<tr><td colspan="2">'';\r\n	else \r\n		$str = ''<tr><td>'';\r\n	\r\n	\r\n	if(!empty($el[''link'']))	\r\n			if(is_numeric($el[''link'']))\r\n				$str .= ''<a href="[~''.$el[''link''].''~]">''.$el[''view_vichet''].''</a></td>'';	\r\n			else\r\n				$str .= ''<a href="''.$el[''link''].''" target="_blank">''.$el[''view_vichet''].''</a></td>'';	\r\n		else $str .= ''<span>''.$el[''view_vichet''].''</span></td>'';\r\n	\r\n	if($el[''pick_out''] == 1) \r\n		$str = substr_replace($str, '' class="last"'', 3, 0);\r\n	\r\n	if($el[''colspan''] == 1)\r\n		$str .= ''<td class="money">''.$el[''price''].''</td><td class="zakaz"></td></tr>'';\r\n	else \r\n		$str .= ''<td class="content">''.$el[''content''].''</td><td class="money">''.$el[''price''].''</td><td class="zakaz"></td></tr>'';\r\n	\r\n	if($el[''addButton''] == 1) \r\n		$str = substr_replace($str, ''<a href="[~3~]">Заказать</a>'', strlen($str)-10, 0);		\r\n	echo $str;\r\n	}			\r\n	echo ''</table>'';		\r\n}\r\n	else ;\r\nreturn;\r\n', 0, '', ' '),
(23, 'SubStr', 'Обрезает текст до нужно величины', 0, 9, 0, '\r\nmb_internal_encoding("UTF-8");\r\n$size= isset($size)? $size:"200";\r\n$docid = (isset($docid)) ? $docid : $modx->documentIdentifier;\r\n$field = isset($field)? $field:"content";\r\n\r\n$docFields = $modx->getDocument($docid);\r\n\r\n$text = $docFields[$field];\r\n$text = strip_tags($text);\r\n$text = mb_substr($text,0,$size);\r\n\r\n$text = explode(" ",$text);\r\narray_pop($text);\r\n$text = implode(" ",$text);\r\nreturn $text.''<span>...</span>'';\r\n', 0, '', ' ');
